	/**
	* giveViewPort
	* Gives the viewport set on content of body:after set in CSS
	* @author Ramon
	**/
	phonak.giveViewPort = function() {
		if (!window.getComputedStyle) {
			window.getComputedStyle = function(el, pseudo) {
				this.el = el;
				this.getPropertyValue = function(prop) {
					var re = /(\-([a-z]){1})/g;
					if (prop == 'float') prop = 'styleFloat';
					if (re.test(prop)) {
						prop = prop.replace(re, function () {
							return arguments[2].toUpperCase();
						});
					}
					return el.currentStyle[prop] ? el.currentStyle[prop] : null;
				};
				return this;
			};
		}

		i = window.getComputedStyle(document.body,':after').getPropertyValue('content');
		if(i !== null) i = i.replace(/['"]/g,'');
		return i;
	};

	phonak.initDealerLocator = function(){
		var keyupDelay = 200;
        var searchingZipCode = false;

		var delay = (function(){
			var timer = 0;
			return function(callback, ms){
				clearTimeout (timer);
				timer = setTimeout(callback, ms);
			};
		})();

        var createAutocomplete = function () {
            // Make sure that the google code is available on the page.
            if (typeof google !== 'undefined') {
                var input = document.getElementById('location');
                var options = searchingZipCode ? {type: '(regions)'} : {type: '(cities)'};
                var onlySearchCountry = !!document.getElementById('onlySearchCountry').value;
                if (onlySearchCountry || searchingZipCode) {
                    var searchCountry = document.getElementById('region').value;
                    // Restrict searches to the websites country.
                    options.componentRestrictions = {country: searchCountry};
                }

                phonak.dealerLocatorAutocomplete = new google.maps.places.Autocomplete(input, options);

                phonak.dealerLocatorAutocomplete.addListener('place_changed', function () {
                    // The user selected a 'place' from the drop down google provided.
                    phonak.dealerLocatorAutocompletePlace = phonak.dealerLocatorAutocomplete.getPlace();
                    // Click the search button.
                    var $search = $(input).parents('.dealer-search-form');
                    $search.trigger('submit');
                });
            }
        };
        createAutocomplete();

        // If the google maps code is not available yet, try to create the autocomplete object later on.
        $('#location').on('keyup', function () {
            var address = $('#location').val();
            if (address && address.length > 0) {
                var previousSearchingZipCode = searchingZipCode;
                var zipCodePatt = new RegExp("^[0-9]+$");
                searchingZipCode = (zipCodePatt.test(address));

                if (previousSearchingZipCode !== searchingZipCode) {
                    if (phonak.dealerLocatorAutocomplete) {
                        phonak.dealerLocatorAutocomplete.unbindAll();
                        google.maps.event.clearInstanceListeners(phonak.dealerLocatorAutocomplete);
                    }
                    phonak.dealerLocatorAutocomplete = null;
                    phonak.dealerLocatorAutocompletePlace = null;
                }
            } else {
                searchingZipCode = false;
            }

            if (phonak.dealerLocatorAutocomplete) {
                // Clear any previously searched and selected places.
                phonak.dealerLocatorAutocompletePlace = null;
            } else {
                // If the autocomplete object is not created yet, create it.
                delay(createAutocomplete, keyupDelay);
            }
        });
	};

