	/**
	* Creates a responsive carousel slider.
	* @param jQuery object : slider elements
	* @param string : (optional) selector to equalize the height of
	*/
	phonak.slider = function ($sliders, strSelectorToEqualize) {

		$sliders.each(function(){
			var $slider = $(this);

			$slider.slick({
				centerMode: false,
				centerPadding: '60px',
				dots: true,
				slidesToShow: $slider.data('slides-to-show') || 3,
				slidesToScroll: 1,
				autoplay: $slider.data('autoplay') || false ,
				autoplaySpeed: ($slider.data('interval') * 1000) || 8000,
				responsive: [
					{
						breakpoint: 1024,
						settings: {
							centerMode: false,
							centerPadding: '40px',
							slidesToShow: $slider.data('slides-to-show') || 3
						}
					},
					{
						breakpoint: 640,
						settings: {
							arrows: false,
							centerMode: false,
							centerPadding: '20px',
							slidesToShow: 1
						}
					}
				]
			});

			var addOverlay = function(){
				var blnOverlay = $slider.data('overlay') || false;

				if (blnOverlay) {
					var $sliderOverlay = $('<div class="slider-overlay"></div>');
					$slider.append($sliderOverlay);

					$sliderOverlay.on('click', function(){
						$slider.getSlick().autoPlay(true);
						$(this).fadeOut();
					});

					$slider.getSlick().options.onAfterChange = function(){
						if ($sliderOverlay.css('display') != 'none') $sliderOverlay.fadeOut();
					};
				}
			};

			addOverlay();

			var addIndex = function(){
				var blnIndex = $slider.data('index') || false,
						strIndexName = $slider.data('index-name') || 'Slide';

				if (blnIndex) {
					var $sliderIndex = $('<div class="slider-index"><span class="slide-name">'+strIndexName+'</span><span class="current-slide">1</span>/<span class="total-slides"></span></div>');

					if ($slider.find('.slick-dots').length) {
						var numSlides = $slider.find('.slick-dots > li').length;
						$sliderIndex.find('.total-slides').text(numSlides);
						$slider.find('.slick-dots').before($sliderIndex);

						$slider.getSlick().options.onBeforeChange = function(slick, currentSlide, nextSlide){
							$sliderIndex.find('.current-slide').text(nextSlide + 1);
						};
					}
				}
			};

			addIndex();

			if (strSelectorToEqualize != null) {
				var equalizeSlidesHeight = function(){
					var $equalizers = $slider.find('.slide ' + strSelectorToEqualize),
							numMaxHeight = 0;

					$equalizers.removeAttr('style');

					$equalizers.each(function(){
						if ($(this).height() > numMaxHeight) numMaxHeight = $(this).height();
					});

					$equalizers.height(numMaxHeight);
				};

				$(window).on('resize load', function(){
					equalizeSlidesHeight();
				});

				equalizeSlidesHeight();
			}

		});
	};


