	/**
	* Shows the matching visual when selecting a color swatch.
	* @param object : (optional) options
	*/
	phonak.wizard = function (options) {

		var options = options || {};

		options.block										= $('.wizard-block');
		options.panels									= options.block.find('.wizard-panel');
		options.targetsList							= options.block.find('.targets-list');
		options.targetsListItems				= options.targetsList.children('li');
		options.targetSelected					= options.block.find('.wizard-target-selected');

		options.situationsList					= options.block.find('.situations-list');
		options.situationsListItems			= options.situationsList.children('li');
		options.selectedSituationsList	= options.block.find('.selected-situations-list');

		options.nav											= options.block.find('.wizard-nav');
		options.navRestart							= options.nav.find('.is-restart');
		options.navNext									= options.nav.find('.is-next');
		options.navShow									= options.nav.find('.is-show');

		options.blnSituationSelected		= false;
		options.strSelectedSituations		= '';

		options.strTagPrefix						= 'phonak:';
		options.strTagFamilyTargets			= options.strTagPrefix + 'targets/';
		options.strTagFamilySituations	= options.strTagPrefix + 'situations/';

		var scrollPageToPanel = function($panel){
			$panel.addClass('is-current');

			var $headerBottom = $('.page-header .header-bottom'),
					blnDocked = $headerBottom.hasClass('is-docked');

			phonak.scrollPageToElement($('.wizard-block .row'), (blnDocked) ? $headerBottom.find('.dockable').height() : 0);
		};

		var goToWizardPanel = function(strDirection){
			if (strDirection === 'first') {
				options.panels.removeClass('is-current');

				options.navRestart.addClass('is-hidden');
				options.navNext.addClass('is-hidden').addClass('is-disabled');
				options.navShow.addClass('is-hidden');

				scrollPageToPanel(options.panels.first());
			} else if (strDirection === 'next') {
				options.panels.each(function(){
					if ($(this).hasClass('is-current')) {
						options.panels.removeClass('is-current');

						var $nextWizardPanel = $(this).next('.wizard-panel');

						if ($nextWizardPanel.length) scrollPageToPanel($nextWizardPanel);
						return false;
					}
				});
			} else if (strDirection === 'last') {
				options.panels.removeClass('is-current');

				options.navRestart.removeClass('is-hidden');
				options.navNext.addClass('is-hidden');
				options.navShow.removeClass('is-hidden');

				options.panels.last().addClass('is-current');
			}
		};

		var checkTargetSelection = function($targetLink){
			options.targetSelected.find('.target-text').text($targetLink.data('text'));
			options.targetSelected.find('.target-icon').removeAttr('class').addClass('target-icon').addClass($targetLink.prop('class'));
		};

		checkTargetSelection(options.targetsList.children('.is-selected').children('a'));

		options.targetsList.find('a').on('click', function(e){
			e.preventDefault();

			var $targetLink = $(this),
					$targetsListItem = $targetLink.parent('li');

			options.targetsListItems.removeClass('is-selected');
			$targetsListItem.addClass('is-selected');

			checkTargetSelection($targetLink);
			checkSituationSelection();

			options.navRestart.removeClass('is-hidden');
			options.navNext.removeClass('is-hidden');
			goToWizardPanel('next');
		});

		var checkSituationSelection = function(){
			options.blnSituationSelected = false;
			options.strSelectedSituations = '';

			options.situationsListItems.each(function(){
				if ($(this).hasClass('is-selected')) {
					options.strSelectedSituations += '<li><span class="situation-holder">' + $(this).find('a').html() + '</span></li>';
					options.blnSituationSelected = true;
				}
			});

			if (options.blnSituationSelected) {
				options.navNext.removeClass('is-disabled');
				options.selectedSituationsList.html(options.strSelectedSituations);
			} else {
				options.navNext.addClass('is-disabled');
			}

			options.blnSituationSelected = false;
			options.strSelectedSituations = '';
		};

		checkSituationSelection();

		var checkBackend = function(){
			var blnBackendDefined = typeof CQ_Analytics != 'undefined';
			return blnBackendDefined;
		}

		var initTagCloud = function(){
			var blnContinue = false;

			if (checkBackend()) {
				if (!CQ_Analytics.TagCloudMgr.isInitialized()) CQ_Analytics.TagCloudMgr.init();
				blnContinue = true;
			}

			return blnContinue;
		}

		var selectTaggedItems = function(strTag, $items){
			$items.children('a').filter(function() {
				return $(this).data('tags') == strTag;
			}).parent('li').addClass('is-selected');
		}

		var checkProps = function(arrProps, strTagFamily, fnCallback, $listItems){
			for (var p=0, len=arrProps.length; p<len; p++) {
				if (arrProps[p].toLowerCase().indexOf(strTagFamily.toLowerCase()) != -1) {
					if (typeof fnCallback === 'function') {
						if ($listItems) {
							fnCallback(arrProps[p], $listItems);
						} else {
							fnCallback(arrProps[p]);
						}
					}
				}
			}
		}

		var setWizardTags = function(strTags, strTagFamily) {
			if (initTagCloud()) {
				checkProps(CQ_Analytics.TagCloudMgr.getPropertyNames(), strTagFamily, CQ_Analytics.TagCloudMgr.removeProperty);

				var arrTags = strTags.split(",");

				if (arrTags.length > 0) {
					for (var t=0, len=arrTags.length; t<len; t++) {
						CQ_Analytics.TagCloudMgr.add(arrTags[t]);
					}
				}

				CQ_Analytics.TagCloudMgr.persist();
			}
		}

		options.situationsList.find('a').on('click', function(e){
			e.preventDefault();

			var $situationLink = $(this),
					$situationsListItem = $situationLink.parent('li');

			$situationsListItem.toggleClass('is-selected');
			checkSituationSelection();
		});

		var checkWizardAlreadyFilled = function() {
			if (initTagCloud()) {
				var arrProps = CQ_Analytics.TagCloudMgr.getPropertyNames();

				if (arrProps.length != 0) {
					checkProps(arrProps, options.strTagFamilyTargets, selectTaggedItems, options.targetsListItems);
					checkProps(arrProps, options.strTagFamilySituations, selectTaggedItems, options.situationsListItems);

					checkTargetSelection(options.targetsList.children('.is-selected').children('a'));
					checkSituationSelection();

					goToWizardPanel('last');
				}
			}
		}

		checkWizardAlreadyFilled();

		options.navRestart.on('click', function(e){
			e.preventDefault();

			goToWizardPanel('first');
		});

		options.navNext.on('click', function(e){
			e.preventDefault();

			var strTargetTags = options.targetsList.children('.is-selected').children('a').eq(0).data('tags'),
					$situationSelectedLinks = options.situationsList.children('.is-selected').children('a'),
					strSituationTags = '';

			$situationSelectedLinks.each(function(index){
				if (index != 0) strSituationTags += ',';
				strSituationTags += $(this).data('tags');
			});

			setWizardTags(strTargetTags, options.strTagFamilyTargets);
			setWizardTags(strSituationTags, options.strTagFamilySituations);

			if (!$(this).hasClass('is-disabled')) {
				options.navRestart.removeClass('is-hidden');
				options.navNext.addClass('is-hidden');
				options.navShow.removeClass('is-hidden');

				goToWizardPanel('next');
			} else {
				alert(options.navNext.data('alert'));
			}

		});

	};


	/**
	* Scales a background image to always fit the entire window but keeping it's aspect ratio.
	* @param jQuery object : element with background image
	*/
	phonak.scaleBackgroundImage = function ($element) {

		if ($element.length) {

			var strBgImgUrl = $element.css('background-image').replace('url(', '').replace(')', '').replace('"', '').replace('"', '');

			if (strBgImgUrl != 'none') {
				var updateBackgroundImage = function(){
					var tempImage = new Image();
					tempImage.src = strBgImgUrl;

					$(tempImage).load(function(){
						var numImgWidth = tempImage.width,
								numImgHeight = tempImage.height,
								numImageRatio = numImgWidth / numImgHeight,
								numCompareRatio;

						if (window.deviceIsIOS) {
							numCompareRatio = $element.outerWidth() / $element.outerHeight()
						} else {
							numCompareRatio = $(window).width() / $(window).height()
						}

						if (numImageRatio <= numCompareRatio) {
							$element.removeClass('is-wide');
						} else {
							$element.addClass('is-wide');
						}
					});
				};

				if (phonak.pageLoaded) {
					updateBackgroundImage();
				} else {
					$(window).on('load.scaleBackgroundImage', function(){
						updateBackgroundImage();
					});
				}

				$(window).on('resize.scaleBackgroundImage', function(){
					updateBackgroundImage();
				});
			}
		}

	};


