	/**
	* Creates a datepicker
	* @param jQuery object : input element on which the datepicker should be added
	*/
	phonak.datepicker = function ($input) {

		if ($input.length) {
			$input.fdatepicker({
				format: $input.data('format') || 'mm/dd/yyyy',
				language: $input.data('language') || 'en'
			});

			$input.change(function() {
				var r = /^(\d{4})\-(\d{1,2})\-(\d{1,2})$/;
				var v = $(this).val();

				if(!r.test(v)) {
					$(this).next('span').show();
				} else {
					$(this).next('span').hide();
				}
			});
		}
	};


