                    strUrl = strUrl.replace($hearingTestBlock.data('fullname-placeholder'), strName);
                    strUrl = strUrl.replace($hearingTestBlock.data('email-placeholder'), strEmail);
                    strUrl = strUrl.replace($hearingTestBlock.data('dob-placeholder'), strDob);
                    strUrl = strUrl.replace($hearingTestBlock.data('gender-placeholder'), strGender);

