	/**
	* Injects calculated specs for the styleguide.
	*/
	phonak.styleguideSpecs = function () {

		var numBaseFontSizePx			= parseInt($('body').css('font-size'), 10),
				$specs								= $('<span>').addClass('specs'),
				arrColors							= [],
				strColorName					= 'color-name',
				strColorCode					= 'color-code';

		$('.styleguide-colors > .color').each(function(){
			var $color							= $(this),
					objColor						= {},
					$colorName					= $color.find('.' + strColorName),
					$colorCode					= $color.find('.' + strColorCode);

			objColor[strColorName] = $colorName.text();
			objColor[strColorCode] = $colorCode.text();

			arrColors.push(objColor);
		});

		var getColorName = function(strColorCodeSelected){
			var strColorNameSelected = 'unknown';

			$.each(arrColors, function(key, value){
				if (strColorCodeSelected === arrColors[key][strColorCode]) strColorNameSelected = arrColors[key][strColorName];
			});

			return strColorNameSelected;
		};



		var createSpec = function(fsPx, fsRem, lhPx, lhRatio, mbPx, mbRem, clName, clCode){
			var strSpec = '';

			strSpec += '<abbr title="font-size">FS</abbr>: ' + fsRem + 'rem (' + fsPx + 'px)';
			strSpec += ', ';
			strSpec += '<abbr title="line-height">LH</abbr>: ' + lhRatio + ' (' + lhPx + 'px)';
			strSpec += ', ';
			strSpec += '<abbr title="margin-bottom">MB</abbr>: ' + mbRem + 'rem (' + mbPx + 'px)';
			strSpec += ', ';
			strSpec += '<abbr title="color">CL</abbr>: ' + clName + ' (' + clCode + ')';
			//strSpec += ', ';

			return strSpec;
		}

		//$('[data-specs]').each(function(){
		$('.styleguide-elements > .row > .column').children().not('.styleguide-category-title, .styleguide-group-title, br').each(function(){
			var $specsElement 			= $(this);
			//var strSpecs 						= $specsElement.data('specs');

			var numFontSizePx 			= parseInt($specsElement.css('font-size'), 10),
					numFontSizeRem 			= numFontSizePx / numBaseFontSizePx,
					numLineHeightPx 		= parseInt($specsElement.css('line-height'), 10),
					numLineHeightRatio 	= numLineHeightPx / numFontSizePx,
					numMarginBottomPx 	= parseInt($specsElement.css('margin-bottom'), 10),
					numMarginBottomRem 	= numMarginBottomPx / numBaseFontSizePx,
					strColorCode 				= phonak.convertRgbToHex($specsElement.css('color')),
					strColorName 				= getColorName(strColorCode);

			$specs.clone().html(createSpec(numFontSizePx, numFontSizeRem, numLineHeightPx, numLineHeightRatio, numMarginBottomPx, numMarginBottomRem, strColorName, strColorCode)).insertBefore($specsElement);

		});



		var $styleguideVisualSizes = $('.styleguide-visual-sizes');

		if ($styleguideVisualSizes.length) {
			var $aspectRatioPlaceholder = $('<img src="' + $styleguideVisualSizes.data('placeholder-url') + '" alt="aspect ratio" />');

			$styleguideVisualSizes.find('.styleguide-group-title').each(function(){
				var $title = $(this),
						arrAspectRatio = $title.data('aspect-ratio').split(':'),
						numHeight = (parseInt($title.css('line-height'), 10) / arrAspectRatio[1]);

				$title.append($aspectRatioPlaceholder.clone().css({
					'width': numHeight * arrAspectRatio[0],
					'height': numHeight * arrAspectRatio[1]
				}));
			});
		}

	};

	/**
	* Prepare collapsible mobile menus
	*/
	phonak.initMobileMenu = function () {

		var menu = jQuery('.left-off-canvas-menu');
		var els = jQuery('.main-nav .sub-nav-list .sub-nav-list', menu);

		// close in reverse (all containers should be visible on load)
		// Hint: .hide() will not add display: none if parent is hidden
		for(var i = els.length - 1; i >= 0; --i) {
			if (!jQuery(els[i]).parent().hasClass('is-current')) {
				jQuery(els[i]).hide();
			}
		}

		els.parent().find('A').each(function (i, el) {

			var $el = jQuery(el);
			var $cnt = $el.parent().find('>UL')
			if ($cnt.length == 0) return;
			var $lnk = jQuery('<a href="#"><div/></a>')
			$lnk.addClass('toggler show-for-small-only');
			if ($cnt.is(':visible')) {
				$lnk.addClass('opened');
			}
			$el.after($lnk);
			$lnk.click(function (e) {
				e.preventDefault();
				$el.parent().find('>UL').slideToggle(400);
				$lnk.toggleClass('opened');
			});

		});

	}

