            var cookieNameSuffix = '';
            if (typeof(fontSuffix) !== 'undefined') {
                cookieNameSuffix = fontSuffix.replace('.min', '');
            }
            var cookieName = 'fonts-loaded' + cookieNameSuffix;
            var cookie = $.cookie(cookieName);
            if (cookie == null) {
                document.documentElement.className += " fonts-loaded";
            }
            // Create a cookie to signify that the browser has downloaded the font-face font and is most likely cached.
            $.cookie(cookieName, cookieNameSuffix, {expires: 2, path: '/'});
